const workspaceId = '9011917991';
const listId = '901109860036';
const apiKey = 'pk_44163283_ZIFREHZQNJWKZ5YSD0XVV45B272AOARG';
const https = require('https');

const logs = [];

function log(message) {
  logs.push({ time: new Date().toISOString(), message });
}

function request(path, method, body = null) {
  log(`Requisição: ${method} ${path}`);

  return new Promise((resolve, reject) => {
    const options = {
      hostname: 'api.clickup.com',
      path,
      method,
      headers: {
        'Content-Type': 'application/json',
        'Authorization': apiKey
      }
    };

    const req = https.request(options, (res) => {
      let data = '';
      res.on('data', chunk => data += chunk);
      res.on('end', () => {
        try {
          log(`Resposta: status ${res.statusCode}`);

          // Tratamento de respostas vazias (comum em DELETEs)
          if (!data || data.trim() === '') {
            return res.statusCode < 300 
              ? resolve({ success: true }) 
              : reject({ 
                  success: false, 
                  message: `Erro: ${res.statusMessage}`, 
                  statusCode: res.statusCode 
                });
          }

          const responseData = JSON.parse(data);

          if (res.statusCode < 300) {
            resolve(responseData);
          } else {
            reject({
              success: false,
              message: `Erro: ${responseData.err || res.statusMessage}`,
              statusCode: res.statusCode
            });
          }
        } catch (error) {
          log(`Erro ao processar resposta: ${error.message}`);
          reject({
            success: false,
            message: `Erro ao processar resposta: ${error.message}`,
            data
          });
        }
      });
    });

    req.on('error', (error) => {
      log(`Erro na requisição: ${error.message}`);
      reject({ success: false, message: `Erro na requisição: ${error.message}` });
    });

    if (body) {
      const data = typeof body === 'string' ? body : JSON.stringify(body);
      log(`Enviando dados: ${data.substring(0, 100)}${data.length > 100 ? '...' : ''}`);
      req.write(data);
    }

    req.end();
  });
}

const operations = {
  async list(query) {
    log('Operação: listar tarefas');

    try {
      // Construir parâmetros de consulta
      const params = [];

      if (query.status) params.push(`statuses[]=${encodeURIComponent(query.status)}`);
      if (query.assignee) params.push(`assignees[]=${encodeURIComponent(query.assignee)}`);
      if (query.due_date_gt) params.push(`due_date_gt=${encodeURIComponent(query.due_date_gt)}`);
      if (query.due_date_lt) params.push(`due_date_lt=${encodeURIComponent(query.due_date_lt)}`);

      // Paginação
      params.push(`page=${query.page || 0}`);
      const limit = Math.min(query.limit || 20, 100);
      params.push(`limit=${limit}`);

      log(`Parâmetros: ${params.join('&')}`);

      // Fazer requisição
      const data = await request(
        `/api/v2/list/${listId}/task?${params.join('&')}`, 
        'GET'
      );

      log(`Tarefas encontradas: ${data.tasks.length}`);

      return {
        success: true,
        tasks: data.tasks,
        total_count: data.tasks.length,
        has_more: data.tasks.length === limit
      };
    } catch (error) {
      log(`Erro: ${error.message || JSON.stringify(error)}`);
      return error;
    }
  },

  async create(query) {
    log('Operação: criar tarefa');

    if (!query.title) {
      log('Erro: Título da tarefa não fornecido');
      return { success: false, message: "Título da tarefa é obrigatório para criação" };
    }

    const body = {
      name: query.title,
      description: query.description || '',
      due_date: query.due_date || null,
      priority: query.priority || 3,
      assignees: query.assignees || []
    };

    try {
      log(`Criando tarefa: "${query.title}"`);
      const data = await request(`/api/v2/list/${listId}/task`, 'POST', body);
      log(`Tarefa criada com sucesso, ID: ${data.id}`);

      return {
        success: true,
        message: "Tarefa criada com sucesso",
        taskId: data.id,
        taskUrl: data.url
      };
    } catch (error) {
      log(`Erro: ${error.message || JSON.stringify(error)}`);
      return error;
    }
  },

  async delete(query) {
    log('Operação: remover tarefa');

    if (!query.task_id) {
      log('Erro: ID da tarefa não fornecido');
      return { success: false, message: "ID da tarefa é obrigatório para remoção" };
    }

    try {
      log(`Removendo tarefa: ${query.task_id}`);
      await request(`/api/v2/task/${query.task_id}`, 'DELETE');
      log('Tarefa removida com sucesso');

      return {
        success: true,
        message: "Tarefa removida com sucesso",
        taskId: query.task_id
      };
    } catch (error) {
      log(`Erro: ${error.message || JSON.stringify(error)}`);
      return error;
    }
  },

  async update(query) {
    log('Operação: atualizar tarefa');

    if (!query.task_id) {
      log('Erro: ID da tarefa não fornecido');
      return { success: false, message: "ID da tarefa é obrigatório para atualização" };
    }

    // Mapear campos
    const fields = {
      title: 'name',
      description: 'description',
      due_date: 'due_date',
      priority: 'priority',
      status: 'status',
      assignees: 'assignees'
    };

    const body = {};

    // Filtrar campos fornecidos
    Object.entries(fields).forEach(([src, dest]) => {
      if (query[src] !== undefined) body[dest] = query[src];
    });

    if (Object.keys(body).length === 0) {
      log('Erro: Nenhum campo fornecido para atualização');
      return { success: false, message: "Nenhum campo fornecido para atualização" };
    }

    try {
      log(`Atualizando tarefa: ${query.task_id}`);
      await request(`/api/v2/task/${query.task_id}`, 'PUT', body);
      log('Tarefa atualizada com sucesso');

      return {
        success: true,
        message: "Tarefa atualizada com sucesso",
        taskId: query.task_id
      };
    } catch (error) {
      log(`Erro: ${error.message || JSON.stringify(error)}`);
      return error;
    }
  }
};

try {
  log(`Iniciando processamento: ${JSON.stringify(query)}`);

  const operation = query.operation || 'create';

  const result = await operations[operation](query);

  return JSON.stringify({
    success: true,
    ...result,
    logs: logs
  });
} catch (error) {
  log(`Erro não tratado: ${error.message || JSON.stringify(error)}`);
  return JSON.stringify({ 
    success: false, 
    message: `Erro não tratado: ${error.message || 'Erro desconhecido'}`,
    logs 
  });
}

