assignments.value:
=<data_hora_atual>
  - Para se localizar no horário e dia atual

    data: {{ $now.format('yyyy-MM-dd') }}
    hora: {{ $now.setLocale(pt-BR).format('HH:mm') }}
    dia_da_semana: {{ $now.weekdayLong }}
    mês: {{ $now.monthLong }}
    final_de_semana:? {{ $now.isWeekend }}
    dias_final_de_semana: sábado e domingo
    timezone: America/São Paulo

{{(() => {
  const n = $now.setLocale('pt-BR').setZone('America/Sao_Paulo');
  const feriados = $json.data;

  return [...Array(7)].map((_, i) => {
    const d = n.plus({ days: i });
    const iso = d.toISODate();
    const label = ['- Hoje é', '- Amanhã será', '- Depois de amanhã será'][i] || `- ${d.weekdayLong} será`;
    const data = d.toFormat('dd/MM/yyyy');
    const hora = i === 0 ? ` - ${d.toFormat('HH:mm')}` : '';

    const feriadoEncontrado = feriados.find(f => f.date === iso);
    const feriado = feriadoEncontrado ? ` (FERIADO: ${feriadoEncontrado.name})` : '';

    return `\t${label} ${d.weekdayLong}, ${data}${feriado}${hora}`;
  }).join('\n');
})()}}
<data_hora_atual>

<feriados_{{ $now.year }}>
{{ $json.data.map(item => {
  const [year, month, day] = item.date.split('-');
  return `\t- ${day}/${month}/${year} -> ${item.name}`;
}).join('\n') }}
</feriados_{{ $now.year }}>
