// Converte o mapeamento recebido em JSON
const mapping = JSON.parse($('Core Input').item.json.body.mapping);

// Verifica se o usuário é admin; caso contrário, retorna erro
if (mapping.system.user_type !== 'admin') {
  return JSON.stringify({ success: false, error: "Acesso negado: usuário não autorizado." });
}

/**
 * Função que espera pelo tempo especificado.
 * @param {number} ms - Milissegundos a aguardar.
 * @returns {Promise<void>}
 */
const delay = ms => new Promise(resolve => setTimeout(resolve, ms));

/**
 * Função para enviar a requisição para criar uma conversa no Directus.
 * Tenta até 3 vezes em caso de falha, com delay de 1 segundo entre as tentativas.
 * @returns {Promise<object>} - Objeto com sucesso ou erro.
 */
async function sendRequest() {
  const url = `http://${mapping.system.directus_service}:8055/items/conversations`;
  const data = {
    user_id: mapping.chatwoot.contact_id,
    date_created: new Date().toISOString()
  };
  const headers = {
    Authorization: `Bearer ${mapping.system.directus_token}`,
    'Content-Type': 'application/json'
  };

  let attempt = 0;
  let lastError = null;

  while (attempt < 3) {
    try {
      const response = await axios.post(url, data, { headers });
      console.log(`Requisição realizada com sucesso na tentativa ${attempt + 1}.`);
      return { success: true, data: response.data };
    } catch (error) {
      attempt++;
      lastError = error;
      console.error(`Tentativa ${attempt} falhou:`, error.response ? error.response.data : error.message);
      if (attempt < 3) {
        console.log("Aguardando 1 segundo antes da nova tentativa...");
        await delay(1000);
      }
    }
  }

  return { success: false, error: lastError.response ? lastError.response.data : lastError.message };
}

return sendRequest().then(result => JSON.stringify(result));

const axios = require('axios');
const dayjs = require('dayjs');
const duration = require('dayjs/plugin/duration');
dayjs.extend(duration);

/**
 * Esta tool configura um timer via webhook.
 * Ela envia, para o endpoint, todo o objeto mapping (obtido em Core Input)
 * e o tempo em segundos calculado a partir dos parâmetros "timer_value" e "timer_unit".
 * Apenas usuários admin podem utilizar esta tool.
 */

// Converte o mapping recebido em JSON
const mapping = JSON.parse($('Core Input').item.json.body.mapping);

// Verifica se o usuário é admin
if (mapping.system.user_type !== 'admin') {
  return JSON.stringify({ success: false, error: "Acesso negado: usuário não autorizado." });
}

// Obtém os parâmetros do timer a partir do input (query)
const timerValue = query.timer_value;  // Número (ex: 30)
const timerUnit = query.timer_unit;      // "s", "m" ou "h"

// Calcula o tempo em segundos usando dayjs.duration
const timerSeconds = dayjs.duration(timerValue, timerUnit).asSeconds();

// Prepara o payload para o webhook
const payload = {
  mapping: mapping,
  timer_seconds: timerSeconds
};

// URL do webhook substituindo 'n8n_webhook' pelo valor da variável no mapping
const url = `http://${mapping.system.n8n_webhook_service}:5678/webhook/timer`;

/**
 * Função para enviar a requisição ao webhook.
 * Tenta enviar uma única vez e retorna o resultado.
 */
async function sendTimerRequest() {
  try {
    console.log(`Enviando requisição para configurar o timer para ${timerSeconds} segundos...`);
    const response = await axios.post(url, payload, {
      headers: { 'Content-Type': 'application/json' }
    });
    console.log("Timer configurado com sucesso.");
    return { success: true, message: `Timer configurado com sucesso. Ele será acionado em ${timerSeconds} segundos.` };
  } catch (error) {
    console.error("Erro ao configurar o timer:", error.response ? error.response.data : error.message);
