const axios = require('axios');

async function runTool() {
  const logArray = [];
  let currentStatus = "";

  try {
    console.log("Obtendo data de última interação e downtime...");
    logArray.push("Obtendo data de última interação e downtime...");

    const dateUpdatedStr = $('Get Vars').first().json.users.date_updated;
    const downtimeStr = $('Get Vars').first().json.ai_agents.downtime;
    console.log(`date_updated: ${dateUpdatedStr}`);
    console.log(`downtime: ${downtimeStr}`);
    logArray.push(`date_updated: ${dateUpdatedStr}`);
    logArray.push(`downtime: ${downtimeStr}`);

    // Se downtime for "00:00:00", considere desativado e não realizar a requisição.
    if (downtimeStr === "00:00:00") {
      console.log("Downtime é 00:00:00. Nenhuma ação será realizada.");
      logArray.push("Downtime é 00:00:00. Nenhuma ação será realizada.");
      currentStatus = $('Consumer').first().json.body.conversation.status;
      return [{ json: { current_status: currentStatus, logs: logArray } }];
    }

    const lastUpdated = new Date(dateUpdatedStr);
    const now = new Date();
    const diffMs = now - lastUpdated;
    const diffMinutes = (diffMs / 60000).toFixed(2);

    // Converter downtime (HH:MM:SS) para milissegundos e para minutos
    const [hours, minutes, seconds] = downtimeStr.split(':').map(Number);
    const downtimeMs = ((hours * 3600) + (minutes * 60) + seconds) * 1000;
    const downtimeMinutes = (downtimeMs / 60000).toFixed(2);

    console.log(`diff: ${diffMinutes} minutes, downtime: ${downtimeMinutes} minutes`);
    logArray.push(`diff: ${diffMinutes} minutes, downtime: ${downtimeMinutes} minutes`);

    // Obter conversation status e conversation_id do nó Consumer
    const conversationStatus = $('Consumer').first().json.body.conversation.status;
    const convId = $('Consumer').first().json.body.conversation.messages[0].conversation_id;
    console.log(`conversation status: ${conversationStatus}`);
    console.log(`conversation_id: ${convId}`);
    logArray.push(`conversation status: ${conversationStatus}`);
    logArray.push(`conversation_id: ${convId}`);

    // Inicialmente, currentStatus recebe o status atual
    currentStatus = conversationStatus;

    // Verifica se o tempo de inatividade foi excedido e se conversation.status é diferente de "pending"
    if (diffMs >= downtimeMs && conversationStatus !== "pending") {
      console.log("Condições atendidas: tempo de inatividade excedido e conversation.status não é 'pending'.");
      logArray.push("Condições atendidas: tempo de inatividade excedido e conversation.status não é 'pending'. Tentando abrir conversa no Chatwoot...");

      const chatwootService = $('Get Vars').first().json.global_vars.chatwoot_service;
      const accountId = $('Consumer').first().json.body.account.id;
      const conversationId = convId;
      const botToken = $('Get Vars').first().json.ai_agents.bot_token;

      const toggleUrl = `http://${chatwootService}:3000/api/v1/accounts/${encodeURIComponent(accountId)}/conversations/${encodeURIComponent(conversationId)}/toggle_status`;
      console.log(`Toggle URL: ${toggleUrl}`);
      logArray.push(`Toggle URL: ${toggleUrl}`);

      try {
        const response = await axios.post(
          toggleUrl,
          { status: "pending" },
          { headers: { api_access_token: botToken } }
        );
        console.log("Conversa aberta com sucesso no Chatwoot.");
        logArray.push("Conversa aberta com sucesso no Chatwoot.");
        currentStatus = "pending";
      } catch (err) {
        console.error("Erro ao abrir conversa:", err.message);
        logArray.push(`Erro ao abrir conversa: ${err.message}`);
      }
    } else {
      console.log("Condições não atendidas: tempo de inatividade não excedido ou conversation.status é 'pending'. Nenhuma ação realizada.");
      logArray.push("Condições não atendidas: tempo de inatividade não excedido ou conversation.status é 'pending'. Nenhuma ação realizada.");
    }

    return [{ json: { current_status: currentStatus, logs: logArray } }];
  } catch (error) {
    console.error("Erro geral:", error.message);
    logArray.push(`Erro geral: ${error.message}`);
    return [{ json: { error: error.message, current_status: currentStatus, logs: logArray } }];
  }
}

return runTool();

const axios = require('axios');

async function main() {
  const trimLog = (msg, limit = 100) => (typeof msg === 'string' && msg.length > limit ? msg.substring(0, limit) + '...' : msg);
  let logs = [];

  try {
    const payload = $('Postgres Trigger').first().json.payload;
    logs.push("Payload carregado.");

    const op = payload.operation;
    logs.push("Operação: " + op);

    let pineconeHost = payload.data.pinecone_host;
    if (!pineconeHost.startsWith("https://")) pineconeHost = "https://" + pineconeHost;

    const pineconeHeaders = {
      "Api-Key": payload.global_vars.pinecone_key,
      "X-Pinecone-API-Version": "2024-10",
      "Content-Type": "application/json"
    };
    logs.push("Headers Pinecone configurados.");

    let endpoint, response, mainResponse;

    const axiosRequest = async (options) => {
      try {
        const res = await axios(options);
        return res.data;
      } catch (err) {
        throw new Error(err?.response?.data?.message || err.message || JSON.stringify(err));
      }
    };

    const getDirectus = async (url, headers, params) => axiosRequest({ method: 'GET', url, headers, params });
    const postDirectus = async (url, headers, data) => axiosRequest({ method: 'POST', url, headers, data });
    const patchDirectus = async (url, headers, data) => axiosRequest({ method: 'PATCH', url, headers, data });

    if (op === "DISABLED" || op === "DELETE") {
      endpoint = pineconeHost + "/vectors/delete";
      const deleteBody = { ids: [payload.data.id], namespace: payload.data.namespace };
      response = await axiosRequest({ method: 'POST', url: endpoint, headers: pineconeHeaders, data: deleteBody });
      mainResponse = response;
    } else if ((op === "UPDATE" || op === "INSERT") && payload.data.enabled === true) {
      let embedding;

      if (op === "INSERT") {
        const directusEndpoint = `http://${payload.global_vars.directus_service}:8055/items/dimensions`;
        const directusHeaders = { Authorization: `Bearer ${payload.global_vars.directus_token}`, "Content-Type": "application/json" };
        const params = { "filter[vector_id][_eq]": payload.data.id };

        const directusRecord = await getDirectus(directusEndpoint, directusHeaders, params);
        if (directusRecord?.data?.length > 0 && directusRecord.data[0].values) {
          const directusValues = directusRecord.data[0].values;
          embedding = typeof directusValues === 'string' ? directusValues.split(",").map(Number) : directusValues;
          logs.push("Embedding reutilizado do Directus (dimensions).");
        }
      }

      if (!embedding) {
        const openAIInput = `Context: ${payload.data.context} Question: ${payload.data.question} Answer: ${payload.data.answer} Similar Questions: ${payload.data.similar_questions?.replace(/,/g, ', ')}`;
        const openAIBody = { input: openAIInput, model: "text-embedding-3-small", dimensions: 1536 };
        const openAIHeaders = { Authorization: `Bearer ${payload.global_vars.openai_key}`, "Content-Type": "application/json" };
        const openAIEndpoint = "https://api.openai.com/v1/embeddings";

        const openAIResponse = await axiosRequest({ method: 'POST', url: openAIEndpoint, headers: openAIHeaders, data: openAIBody });
        embedding = openAIResponse?.data?.[0]?.embedding;
        if (!embedding) throw new Error("Embedding não retornado pelo OpenAI.");
      }

      const similarQuestionsArray = payload.data.similar_questions?.split(',').map(s => s.trim()) || [];

      endpoint = pineconeHost + "/vectors/upsert";
      const upsertBody = {
        vectors: [{
          id: payload.data.id,
          values: embedding,
          metadata: {
            a: payload.data.answer,
            t: payload.data.context,
            q: payload.data.question,
            sq: similarQuestionsArray
          }
        }],
        namespace: payload.data.namespace
      };

      response = await axiosRequest({ method: 'POST', url: endpoint, headers: pineconeHeaders, data: upsertBody });
      mainResponse = response;

      // Atualização/Criação Directus
      const dimensionsUrl = `http://${payload.global_vars.directus_service}:8055/items/dimensions`;
      const dimensionsHeaders = { Authorization: `Bearer ${payload.global_vars.directus_token}`, "Content-Type": "application/json" };
      const dimensionsParams = { "filter[vector_id][_eq]": payload.data.id };
      const dimensionsRecord = await getDirectus(dimensionsUrl, dimensionsHeaders, dimensionsParams);

      const directusValues = Array.isArray(embedding) ? embedding.join(",") : embedding;
      const timestamp = new Date().toISOString();
      const dimensionsBody = { values: directusValues, vector_id: payload.data.id, date_created: timestamp, date_updated: timestamp };

      if (dimensionsRecord?.data?.length > 0) {
        const id = dimensionsRecord.data[0].id;
        await patchDirectus(`${dimensionsUrl}/${id}`, dimensionsHeaders, dimensionsBody);
      } else {
        await postDirectus(dimensionsUrl, dimensionsHeaders, dimensionsBody);
      }
    }

    return [{ json: { mainResponse, logs } }];
  } catch (error) {
    logs.push("Erro: " + (error?.message || JSON.stringify(error)));
    return [{ json: { error: error?.message || String(error), logs } }];
  }
}

return main();
